#define UserError 0x0000                                                                  //06-07-22|0x0034
#define BuildError 0x7fff                                                                 //06-07-22|0x0034
#define Display 0x7ffe                                                                    //06-07-22|0x0034
#define KeyBoard 0x7ffd                                                                   //06-07-22|0x0034
#define Success 0xffff                                                                    //06-07-22|0x0034
#define SystemError 0x8000                                                                //06-07-22|0x0034
#define Continue 0x0001                                                                   //06-07-22|0x0034
#define FileDone 0x0002                                                                   //06-07-22|0x0034
#define LineDone 0x0003                                                                   //06-07-22|0x0034
#define Yes 0x0004                                                                        //06-07-22|0x0034
#define No 0x0005                                                                         //06-07-22|0x0034
#define RepeatChar '\x6'                                                                  //06-07-22|0x0034
#define Great 0x0007                                                                      //06-07-22|0x0034
#define Equal 0x0008                                                                      //06-07-22|0x0034
#define Less 0x0009                                                                       //06-07-22|0x0034
#define Zero 0x000a                                                                       //06-07-22|0x0034
#define Minus 0x000b                                                                      //06-07-22|0x0034
//***************************************************************************             //06-07-22|0x0035
#define SystemMsg PcgSystemError("precform", __LINE__);                                   //06-07-22|0x0036
#define ReturnSystemError { SystemMsg; return(SystemError); }                             //06-07-22|0x0036
#define ReturnSystemAddrError { SystemMsg; SystemError; return(0); }                      //06-07-22|0x0036
#define _SwitchErr case UserError: return(UserError)                                      //06-07-22|0x0037
#define _SwitchOk case Success: break                                                     //06-07-22|0x0037
#define _SwitchEnd default: ReturnSystemError                                             //06-07-22|0x0037
#define _SwitchBase _SwitchOk; _SwitchErr; _SwitchEnd                                     //06-07-22|0x0037
#define _SwitchYes case Yes:      return(Yes)                                             //06-07-22|0x0037
#define _SwitchNo case No:       return(No)                                               //06-07-22|0x0037
#define _SwitchGroup { _SwitchBase; }                                                     //06-07-22|0x0037
#define _SwitchGotoLoop _SwitchGroup goto loop                                            //06-07-22|0x0037
#define _SwitchReturn _SwitchGroup return(Success)                                        //06-07-22|0x0037
#define _SwitchBreak _SwitchGroup break                                                   //06-07-22|0x0037
#define _SwitchYesNo { _SwitchYes; _SwitchNo; _SwitchErr; _SwitchEnd; }                   //06-07-22|0x0037
#define BreakFinish(_a) status = Finish(_a); break                                        //06-07-22|0x0038
#define ReturnFinish(_a) return(Finish(_a))                                               //06-07-22|0x0038
#define ReturnFinishBuild(_a) { Finish(_a);  return(BuildError); }                        //06-07-22|0x0038
#define ReturnFinishAddr(_a) { Finish(_a);  return(0L); }                                 //06-07-22|0x0038
